package ro.sapca.recipeapp.repository.recipeRepo;

import java.util.ArrayList;
import java.util.List;

import ro.sapca.recipeapp.domain.Recipe;

public class RecipeInMemoryRepository implements RecipeRepository{

    private static final RecipeInMemoryRepository instance = new RecipeInMemoryRepository();
    List<Recipe> recipes = new ArrayList<>();

    public static RecipeInMemoryRepository getRecipeInMemoryRepository() {
        return instance;
    }

    private RecipeInMemoryRepository() {
        Recipe recipe1 = new Recipe(1L, "Salata", 60, 15, 4, "salata, rosii, castraveti", "se taie ingredientele si se pun in bol", 129, "catalina",
                "data:image/jpeg;base64,");
        Recipe recipe2 = new Recipe(2L, "Sos", 10, 7, 4, "maioneza, mustar, piper", "se amesteca", 240, "catalina", "data:image/jpeg;base64,");

        recipes.add(recipe1);
        recipes.add(recipe2);
    }

    @Override
    public Recipe findRecipeById(Long id) {
        for (Recipe recipe: recipes) {
            if(recipe.getId()==id)
                return recipe;
        }
        return null;
    }

    @Override
    public Iterable<Recipe> findAllRecipes() {
        return recipes;
    }

    @Override
    public Iterable<Recipe> findAllRecipesOfUser(String username) {
        List<Recipe> optional = new ArrayList<>();
        for (Recipe recipe: recipes) {
            if(recipe.getCreatorUsername().equals(username))
                optional.add(recipe);
        }
        return optional;
    }

    @Override
    public Iterable<Recipe> findAllRecipesOfUserByName(String username, String recipeName) {
        List<Recipe> optional = new ArrayList<>();
        for (Recipe recipe: recipes) {
            if(recipe.getCreatorUsername().equals(username) && recipe.getName().equals(recipeName))
                optional.add(recipe);
        }
        return optional;
    }

    @Override
    public boolean save(Recipe recipe) {
        return recipes.add(recipe);
    }

    @Override
    public boolean delete(Long id) {
        return recipes.removeIf(recipe->(recipe.getId().equals(id)));
    }

    @Override
    public boolean deleteRecipeOfUser(Long id, String username) {
        return recipes.removeIf(recipe->(recipe.getId().equals(id) && recipe.getCreatorUsername().equals(username)));
    }

    @Override
    public boolean update(Recipe recipe) {
        for (Recipe r: recipes) {
            if(r.getId().equals(recipe.getId())) {
                r.setCookingTimeInMinutes(recipe.getCookingTimeInMinutes());
                r.setPreparationTimeInMinutes(recipe.getPreparationTimeInMinutes());
                r.setServings(recipe.getServings());
                r.setDescription(recipe.getDescription());
                r.setIngredients(recipe.getIngredients());
                r.setKcalPer100(recipe.getKcalPer100());
                r.setName(recipe.getName());
                return true;
            }
        }
        return false;
    }
}
